GO

--
-- Tabelle interessenten
--

DROP TABLE IF EXISTS interessenten;
CREATE TABLE interessenten (
  Interessent_id INT NOT NULL PRIMARY KEY IDENTITY(1,1),
  Standort VARCHAR(20) DEFAULT NULL,
  PLZ CHAR(5) DEFAULT NULL,
  Adresse_Standort VARCHAR(20) DEFAULT NULL,
  KFZ CHAR(4) DEFAULT NULL,
  Abteilung VARCHAR(20) DEFAULT NULL,
  Anrede VARCHAR(15) DEFAULT NULL,
  Vorname VARCHAR(30) NOT NULL,
  Nachname VARCHAR(40) NOT NULL,
  EMail VARCHAR(60) NOT NULL,
  Benutzeranmeldename VARCHAR(30) NOT NULL
);





